static QState SumoHSM_CalibWait_e(SumoHSM * const me) {
    BSP_ledOn();
    BSP_motors(0,0);
    BSP_ledStripeSetStrategyColor(me->strategy);
    me->calib_status = 0;
    QTimeEvt_armX(&me->timeEvt, BSP_TICKS_PER_MILISSEC * 250, 0);
    return QM_ENTRY(&SumoHSM_CalibWait_s);
}
/*${AOs::SumoHSM::SM::CalibWait} */
static QState SumoHSM_CalibWait_x(SumoHSM * const me) {
    QTimeEvt_disarm(&me->timeEvt);
    return QM_EXIT(&SumoHSM_CalibWait_s);
}
/*${AOs::SumoHSM::SM::CalibWait} */
static QState SumoHSM_CalibWait(SumoHSM * const me, QEvt const * const e) {
    QState status_;
    switch (e->sig) {
        /*${AOs::SumoHSM::SM::CalibWait::START} */
        case START_SIG: {
            /*${AOs::SumoHSM::SM::CalibWait::START::[strategy_0||strategy_1]} */
            if (me->strategy == 0 || me->strategy == 1) {
                static struct {
                    QMState const *target;
                    QActionHandler act[3];
                } const tatbl_ = { /* tran-action table */
                    &SumoHSM_CalibTurn_s, /* target state */
                    {
                        Q_ACTION_CAST(&SumoHSM_CalibWait_x), /* exit */
                        Q_ACTION_CAST(&SumoHSM_CalibTurn_e), /* entry */
                        Q_ACTION_NULL /* zero terminator */
                    }
                };
                status_ = QM_TRAN(&tatbl_);
            }
            /*${AOs::SumoHSM::SM::CalibWait::START::[strategy_2]} */
            else if (me->strategy == 2) {
                static struct {
                    QMState const *target;
                    QActionHandler act[3];
                } const tatbl_ = { /* tran-action table */
                    &SumoHSM_CalibFront_s, /* target state */
                    {
                        Q_ACTION_CAST(&SumoHSM_CalibWait_x), /* exit */
                        Q_ACTION_CAST(&SumoHSM_CalibFront_e), /* entry */
                        Q_ACTION_NULL /* zero terminator */
                    }
                };
                status_ = QM_TRAN(&tatbl_);
            }
            else {
                status_ = QM_UNHANDLED();
            }
            break;
        }
        /*${AOs::SumoHSM::SM::CalibWait::RADIO_EVT_1} */
        case RADIO_EVT_1_SIG: {
            static struct {
                QMState const *target;
                QActionHandler act[3];
            } const tatbl_ = { /* tran-action table */
                &SumoHSM_Idle_s, /* target state */
                {
                    Q_ACTION_CAST(&SumoHSM_CalibWait_x), /* exit */
                    Q_ACTION_CAST(&SumoHSM_Idle_e), /* entry */
                    Q_ACTION_NULL /* zero terminator */
                }
            };
            BSP_ledStripeSetAll(0x94, 0x00, 0xD3);
            status_ = QM_TRAN(&tatbl_);
            break;
        }
        /*${AOs::SumoHSM::SM::CalibWait::TIMEOUT} */
        case TIMEOUT_SIG: {
            static bool time_long;

            BSP_ledToggle();

            if (time_long){
                QTimeEvt_armX(&me->timeEvt, BSP_TICKS_PER_MILISSEC * 150, 0);
            } else {
                QTimeEvt_armX(&me->timeEvt, BSP_TICKS_PER_MILISSEC * 1000, 0);
            }
            time_long = !time_long;


            status_ = QM_HANDLED();
            break;
        }
        /*${AOs::SumoHSM::SM::CalibWait::RADIO_EVT_2} */
        case RADIO_EVT_2_SIG: {
            SumoHSM_change_strategy(me);
            BSP_ledStripeSetStrategyColor(me->strategy);
            status_ = QM_HANDLED();
            break;
        }
        default: {
            status_ = QM_SUPER();
            break;
        }
    }
    return status_;
}

